mapper.update:
UPDATE t_goods_category
SET parent_id = #{parentId}
where goods_category_id = #{goodsCategoryId}

mapper.select:
select * from t_goods_category
parent_id = #{parentId}
and ( name like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%') or keywords like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%') or description like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%'))
order by sort asc
, ${orderColumn} ${orderType}
