mapper.select:
select g.goods_id, g.name, g.store_name, g.price, g.title_img, g.salenum, g.carriage, g.area, g.type
, (POWER(MOD(ABS(s.live_store_gps_lng - #{lng}),360),2) + POWER(ABS(s.live_store_gps_lat - #{lat}),2)) AS distance
from t_goods g inner join t_store s on g.store_id = s.store_id
g.type = #{type}
and ( g.name like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%') or g.ad_word like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%') or g.store_name like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%'))
order by ${order}
